.woocommerce .woocommerce-result-count {
    margin: 0;
}
.woocommerce .woocommerce-result-count {
    font: 400 14px/2.5em "Lato", Arial, sans-serif;
}
.woocommerce select {
    background: #ufc3;
    border: 1px solid #ufc7;
    color: #ufc0;
    font: 400 14px/2.5em "Lato", Arial, sans-serif;
    height: 35px;
}
.woocommerce .products ul,
.woocommerce ul.products {
    float: left;
    margin: 40px 0 0;
    width: 100%;
}
.woocommerce ul.products li.product,
.woocommerce-page ul.products li.product {
    margin-bottom: 40px;
}
.woocommerce ul.products li.product a img {
    backface-visibility: hidden;
    margin: 0 0 20px;
    opacity: 1;
    transition: opacity .3s;
}
.woocommerce ul.products li.product img:hover {
    opacity: .7;
}
.woocommerce ul.products li.product h3 {
    font: 300 25px/1.4em \\\'Lato\\\', Arial, sans-serif;
    margin: 0 0 10px;
    padding: 0;
}
.woocommerce .products .star-rating {
    margin: 0 0 10px;
}
.woocommerce ul.products li.product .star-rating {
    font-size: 12px;
}
.woocommerce ul.products li.product .price {
    color: #ufc0;
    font: 700 18px/25px \\\'Lato\\\', Arial, sans-serif;
    margin: 0;
}
.woocommerce ul.products li.product .price ins {
    display: inline-block;
}
.woocommerce ul.products li.product.has-children .price del {
    display: block;
    margin-right: 0;
}
.woocommerce ul.products li.product.has-children .price ins {
    display: block;
}
.woocommerce ul.products li.product.sale .price del {
    color: #ufc9;
    display: inline-block;
    font-weight: 400;
    margin-right: 6px;
    opacity: .7;
}
.woocommerce ul.products li.product.sale .price ins {
    color: #ufc8;
}
.woocommerce ul.products li.product .button {
    margin-top: 10px;
}
.woocommerce ul.products li.product .onsale {
    left: auto;
    margin: -10px -10px 0 0;
}
.woocommerce ul.products li.product a.added_to_cart {
    background: #ufc6;
    color: #ufc0;
    margin-top: 10px;
}
.woocommerce ul.products li.product a.added_to_cart:focus,
.woocommerce ul.products li.product a.added_to_cart:hover {
    background: #ufc7;
}
.woocommerce nav.woocommerce-pagination {
    clear: both;
    margin-top: 20px;
}
.woocommerce nav.woocommerce-pagination ul {
    border: none;
    margin: 0;
    position: relative;
    width: 100%;
}
.woocommerce nav.woocommerce-pagination ul li {
    border: none;
    float: none;
}
.woocommerce nav.woocommerce-pagination ul li a,
.woocommerce nav.woocommerce-pagination ul li span {
    color: #ufc0;
    display: block;
    font: 400 14px/2.143em "Lato", Arial, sans-serif;
    margin: 5px 10px 5px 0;
    padding: 0;
}
.woocommerce nav.woocommerce-pagination ul li span.current {
    background: #ufc0;
    border-radius: 50%;
    color: #ufc3;
    height: 30px;
    width: 30px;
}
.woocommerce nav.woocommerce-pagination ul li a:focus,
.woocommerce nav.woocommerce-pagination ul li a:hover {
    background: transparent;
    color: #ufc0;
}
.woocommerce nav.woocommerce-pagination ul li a.next,
.woocommerce nav.woocommerce-pagination ul li a.prev {
    background-color: #ufc6;
    border-radius: 50%;
    height: 40px;
    margin: 0;
    overflow: hidden;
    text-align: center;
    text-indent: 100%;
    transition: background-color .25s;
    white-space: nowrap;
    vertical-align: middle;
    width: 40px;
}
.woocommerce nav.woocommerce-pagination ul li a.prev {
    margin: 0 10px 0 0;
}
.woocommerce nav.woocommerce-pagination ul li a.next:before,
.woocommerce nav.woocommerce-pagination ul li a.prev:before {
    background: url("UPFRONT_THEME_BASE/ui/gillie-sprite_v2.png") no-repeat;
    background: url("UPFRONT_THEME_BASE/ui/gillie-sprite_v2.svg"), none;
    content: "";
    display: block;
    height: 20px;
    margin: 10px auto;
    width: 10px;
}
.woocommerce nav.woocommerce-pagination ul li a.next:before {
    background-position: -235px -70px;
}
.woocommerce nav.woocommerce-pagination ul li a.prev:before {
    background-position: -75px -70px;
}
.woocommerce nav.woocommerce-pagination ul li a.next:hover,
.woocommerce nav.woocommerce-pagination ul li a.prev:hover {
    background-color: #ufc7;
}
/* WooCommerce: Responsive */
@media only screen and (min-width: 768px) and (max-width: 1079px) {
    /* WooCommerce: Products */
    .woocommerce ul.products li.product .button.product_type_external {
        max-width: 110px;
        min-width: inherit;
        overflow: hidden;
        padding: 0 5px;
        text-overflow: ellipsis;
        white-space: nowrap;
    }
    /* WooCommerce: Checkout */
    div#page .upfront-output-wrapper .upost-data-object-post_data .upfront-output-object .woocommerce ul.order_details li {
        margin: 0;
        padding: 0;
        width: 50%;
    }
    div#page .upfront-output-wrapper .upost-data-object-post_data .upfront-output-object .woocommerce ul.order_details li:first-child {
        padding: 0 20px 10px 0;
    }
    div#page .upfront-output-wrapper .upost-data-object-post_data .upfront-output-object .woocommerce ul.order_details li:nth-child(2n) {
        border-right: none;
        padding: 0 0 10px 20px;
    }
    div#page .upfront-output-wrapper .upost-data-object-post_data .upfront-output-object .woocommerce ul.order_details li:nth-child(3n) {
        border-top: 1px dashed #ufc0;
        padding: 10px 20px 0 0;
    }
    div#page .upfront-output-wrapper .upost-data-object-post_data .upfront-output-object .woocommerce ul.order_details li:last-child {
        border-top: 1px dashed #ufc0;
        border-right: none;
        padding: 10px 0 0 20px;
    }
}
@media only screen and (max-width: 768px) {
    /* WooCommerce: Responsive Global Styles */
    .woocommerce .col2-set .col-2 {
        margin-top: 20px;
    }
    .woocommerce form .form-row-first,
    .woocommerce form .form-row-last {
        float: none;
        padding: 0;
        width: 100%;
    }
    .woocommerce table.shop_table_responsive tr:nth-child(2n) td {
        background: #ufc6;
    }
    .woocommerce table.shop_table_responsive tr td:before {
        margin-right: 10px;
    }
    div#page .upfront-output-wrapper .upost-data-object-post_data .upfront-output-object .woocommerce table.cart tbody tr:first-child td,
    div#page .upfront-output-wrapper .upost-data-object-post_data .upfront-output-object .woocommerce table.account-orders-table tbody tr:first-child td {
        border-top: 1px solid #ufc7;
    }
    div#page .upfront-output-wrapper .upost-data-object-post_data .upfront-output-object .woocommerce table.shop_table tbody td.product-quantity {
        line-height: 35px;
    }
    /* WooCommerce: Cart */
    .woocommerce table.shop_table_responsive.cart tr:last-child td {
        background: transparent;
    }
    .woocommerce #content table.cart td.actions .coupon,
    .woocommerce table.cart td.actions .coupon {
        margin-bottom: 10px;
        padding: 0;
    }
    div#page .upfront-output-wrapper .upost-data-object-post_data .upfront-output-object .woocommerce table.cart td.actions .coupon .button {
        float: right;
    }
    div#page .upfront-output-wrapper .upost-data-object-post_data .upfront-output-object .woocommerce table.cart td.actions > input[type="submit"].button {
        float: none;
    }
    /* WooCommerce: Checkout */
    div#page .upfront-output-wrapper .upost-data-object-post_data .upfront-output-object .woocommerce #customer_details .col-2 {
        border-top: 1px solid #ufc7;
        margin-top: 40px;
        padding-top: 40px;
    }
    div#page .upfront-output-wrapper .upost-data-object-post_data .upfront-output-object .woocommerce .checkout_coupon .button {
        margin: 10px 0 0;
        width: 100%;
    }
    div#page .upfront-output-wrapper .upost-data-object-post_data .upfront-output-object .woocommerce ul.order_details li {
        border-top: 1px dashed #ufc0;
        border-right: none;
        display: block;
        float: none;
        margin: 10px 0 0;
        padding: 10px 0 0;
        text-align: center;
    }
    div#page .upfront-output-wrapper .upost-data-object-post_data .upfront-output-object .woocommerce ul.order_details li:first-child {
        border-top: none;
        margin-top: 0;
    }
    div#page .upfront-output-wrapper .upost-data-object-post_data .upfront-output-object .woocommerce #shipping_postcode_field {
        margin: 0 0 15px;
    }
    /* WooCommerce: My Account */
    .woocommerce .woocommerce-MyAccount-navigation {
        margin-bottom: 40px;
    }
    .woocommerce #customer_login .u-column2.col-2 {
        margin-top: 40px;
    }
}
@media only screen and (max-width: 569px) {
    /* WooCommerce: Products */
    .woocommerce .woocommerce-ordering,
    .woocommerce .woocommerce-result-count {
        float: none;
        width: 100%;
    }
    .woocommerce .woocommerce-result-count {
        margin-bottom: 5px;
    }
    .woocommerce .woocommerce-ordering select {
        width: 100%;
    }
    .woocommerce ul.products li.product {
        float: none;
        width: 100%;
    }
    /* WooCommerce: Cart */
    .woocommerce .cart-collaterals .cross-sells ul.products li {
        width: 100%;
    }
}
